import React from 'react';
import { Link } from 'react-router-dom';

const sizeMap = {
  sm: {
    logo: 'w-6 h-6 text-base',
    text: 'text-lg',
    gap: 'ml-2',
  },
  md: {
    logo: 'w-8 h-8 text-lg',
    text: 'text-xl',
    gap: 'ml-2',
  },
  lg: {
    logo: 'w-12 h-12 text-2xl',
    text: 'text-2xl',
    gap: 'ml-3',
  },
};

const BrandLogo = ({ size = 'md', to = '/' }) => {
  const s = sizeMap[size] || sizeMap.md;
  return (
    <Link to={to} className={`flex items-center group`}>
      <div className={`bg-primary-600 rounded-lg flex items-center justify-center ${s.logo}`}> 
        <span className="text-white font-bold">V</span>
      </div>
      <span className={`${s.gap} font-bold ${s.text} text-primary-700 group-hover:text-primary-800 transition-colors`}>VishwasJobPortal</span>
    </Link>
  );
};

export default BrandLogo;
